import React, { Component }  from 'react';
import logo from './logo.svg';
import './App.css';
import { stat } from 'fs';

class Table extends Component {
    constructor(props){
      super(props);
    this.state = {properti: []};
    this.deleteSiswa = this.deleteSiswa.bind(this);
    }

    render(){
      return(
      <div className="container">
        <h3 style={{textAlign: "center"}} >Tabel Siswa</h3>
        <table className="table table-hovered table-striped table-bordered">
          <thead>
            <tr>
              <th>No</th><th>Nama</th><th>Alamat</th><th>Hobi</th><th>Action</th>
            </tr>
          </thead>
          <tbody>
            {this.props.properti.map((val,index) => (
              <tr>
                <td>{index+1}</td>
                <td>{val.nama}</td>
                <td>{val.alamat}</td>
                <td>{val.hobi}</td>
                <td><button onClick={this.deleteSiswa} className="btn btn-danger">Delete</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      );
    }

    deleteSiswa(e){
      var value = this.props.properti.indexOf(e);
      this.props.properti.splice(value,1);
      this.setState({properti: this.props.properti});
    }
}


class InputData extends Component {
  constructor(props){
    super(props);
    this.state = {properti:[{nama: "Alfansyah",alamat: "Jalan Pahlawan",hobi: "Makar"}],nama: "",alamat: "",hobi: ""};  
    this.cekSubmit = this.cekSubmit.bind(this);
    this.changeNama = this.changeNama.bind(this);
    this.changeAlamat = this.changeAlamat.bind(this);
    this.changeHobi = this.changeHobi.bind(this);
  }

  render(){
    return(
      <div className="container col-md-6">
        <Table properti={this.state.properti} />
      <h3>Tambah Data</h3>

          <form onSubmit={this.cekSubmit}>
          <div className="form-group">
            <label htmlFor="nama">Nama</label>
            <input onChange={this.changeNama} id="nama" name="nama" type="text" className="form-control" />
          </div>
          <div className="form-group">
            <label htmlFor="alamat">Alamat</label>
            <input onChange={this.changeAlamat} id="alamat" name="alamat" type="text" className="form-control" defaultValue={this.state.alamat} />
          </div>
          <div className="form-group">
            <label htmlFor="hobi">Hobi</label>
            <input onChange={this.changeHobi} id="hobi" name="hobi" type="text" className="form-control" defaultValue={this.state.hobi} />
          </div>
          <button type="submit">Tambah</button>
        </form>     

      </div>
    );
  }

  changeNama(e){
    this.setState({nama: e.target.value});
  }

  changeAlamat(e){
    this.setState({alamat: e.target.value});
  }

  changeHobi(e){
    this.setState({hobi: e.target.value});
  }

  cekSubmit(e){
    e.preventDefault();
    if(!this.state.nama){
      return alert('Nama kosong bos!')
    }
    const copyArray = this.state.properti.slice();
    copyArray.push({nama: this.state.nama,alamat: this.state.alamat,hobi: this.state.hobi});
    this.setState({properti: copyArray })
    console.log(copyArray)
  }
}

export default InputData;
