import React, { Component }  from 'react';
import logo from './logo.svg';
import './App.css';

class Table extends Component {
    constructor(props){
      super(props);
      this.state = {properti:[{no:0,nama:"Alfan",alamat:"Jalan Pahlawan",hobi:"Memancing"}]};
    }

    render(){
      return(
      <div className="container">
        <h3 style={{textAlign: "center"}} >Tabel Siswa</h3>
        <table style={{margin: 20+'px'}} className="table table-hovered table-striped table-bordered">
          <thead>
            <tr>
              <th>No</th><th>Nama</th><th>Alamat</th><th>Hobi</th>
            </tr>
          </thead>
          <tbody>
            {this.state.properti.map((val,index) => (
              <tr>
                <td>{index+1}</td>
                <td>{val.nama}</td>
                <td>{val.alamat}</td>
                <td>{val.hobi}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <InputData />
      </div>
      );
    }
}

export default Table;

class InputData extends Component {
  constructor(props){
    super(props);
    this.state = {properti:[],nama: "",alamat: "",hobi: ""};

    this.cekSubmit = this.cekSubmit.bind(this);
    this.changeNama = this.changeNama.bind(this);
  }

  render(){
    return(
      <div className="col-md-3">
      <h3>Tambah Data</h3>

          <form onSubmit={this.cekSubmit}>
          <div className="form-group">
            <label htmlFor="nama">Nama</label>
            <input onChange={this.changeNama} id="nama" name="nama" type="text" className="form-control" />
          </div>
          <div className="form-group">
            <label htmlFor="alamat">Alamat</label>
            <input onChange={this.changeAlamat} id="alamat" name="alamat" type="text" className="form-control" defaultValue={this.state.alamat} />
          </div>
          <div className="form-group">
            <label htmlFor="hobi">Hobi</label>
            <input onChange={this.changeHobi} id="hobi" name="hobi" type="text" className="form-control" defaultValue={this.state.hobi} />
          </div>
          <button type="submit">Tambah</button>
        </form>

      </div>
    );
  }

  changeNama(e){
    this.setState({nama: e.target.value});
  }

  changeAlamat(e){
    this.setState({alamat: e.target.value});
  }

  changeHobi(e){
    this.setState({hobi: e.target.value});
  }

  cekSubmit(e){
    e.preventDefault();
    this.setState({
      properti: [...this.state.properti,{nama: this.state.nama,alamat: this.state.alamat,hobi: this.state.hobi}]
    })
    console.log(this.state.properti);
  }
}
